import React, { Component } from 'react';
import {Switch, Route} from 'react-router-dom';
import ArticleList from './ArticleList';
import './App.css';

class App extends Component {
  static defaultProps = {
    articles: [
      {
        url: "https://www.artofmanliness.com/articles/the-prisoner-workout/",
        word_count: 15008
      },
      {
        url: "https://fs.blog/2017/03/seneca-on-the-shortness-of-time/",
        word_count: 5484
      },
      {
        url: "https://www.artofmanliness.com/articles/20-rules-for-walking/",
        word_count: 5810
      }
    ]
  };
  render() {
    const articleBlock = this.props.articles.map(article => (
      <li>
        URL - {article.url} WORD COUNT - {article.word_count}
      </li>
    ));

    return <Route path='/articles' render={() => <ArticleList articles={this.props.articles}/>} />;
    // return (
    //   <div>
    //     <div className='container'>
    //       {articleBlock}
    //     </div>
    //   </div>
    // );
  }
}

export default App;
